import React from 'react';
import Link from 'next/link';
import {ForceGraph, ForceGraphNode, ForceGraphLink} from 'react-vis-force';
import fetch from 'isomorphic-unfetch';
//import Tree from 'react-d3-tree';
//const Tree = require('react-d3-tree').Tree;

const myTreeData = [
  {
    name: 'Top Level',
    attributes: {
      keyA: 'val A',
      keyB: 'val B',
      keyC: 'val C',
    },
    children: [
      {
        name: 'Level 2: A',
        attributes: {
          keyA: 'val A',
          keyB: 'val B',
          keyC: 'val C',
        },
      },
      {
        name: 'Level 2: B',
      },
    ],
  },
];

const PersonLink = props => (
  <li>
    <Link href="/person/[name]" as={`/person/${props.name}`}>
      <a>{props.name}</a>
    </Link>
  </li>
);

const Index = props => {
  const Tree = require('react-d3-tree').Tree;
  return (
    <div>
      <ForceGraph simulationOptions={{ height: 300, width: 300 }}>
        {props.shows.map(person => (
          <ForceGraphNode node={{ id: person.id, name: person.name }} fill="red" />
        ))}
      </ForceGraph>
      <ForceGraphLink link={{ source: 'first-node', target: 'second-node' }} />
      <ul>
        <PersonLink name="Lev Parnas" />
        <PersonLink name="Donald Trump" />
        <PersonLink name="Jared Kushner" />
      </ul>
      <ul>
      {props.shows.map(person => (
        <li key={person.id}>
          <Link href="/person/[name]" as={`/person/${person.name}`}>
            <a>{person.name}</a>
          </Link>
        </li>
      ))}
    </ul>
    <div id="treeWrapper" style={{width: '50em', height: '20em'}}>

    <Tree data={myTreeData} onClick={this.onClick} />

</div>    </div>
)};

Index.getInitialProps = async function() {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  let d3Tree = await import("react-d3-tree");
  Tree = d3Tree.Tree;

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    props: [],
    shows: data.map(entry => entry.show),
    renderTree: true,
  };
};

export default Index;
