import React from 'react';
import ReactDOM from 'react-dom';
import Link from 'next/link';
import {ForceGraph, ForceGraphNode, ForceGraphLink} from 'react-vis-force';
import fetch from 'isomorphic-unfetch';
import * as d3 from "d3";

var width = 960;
var height = 500;
var force = d3.forceSimulation();

// *****************************************************
// ** d3 functions to manipulate attributes
// *****************************************************

var enterNode = (selection) => {
  selection.classed('node', true);

  selection.append('circle')
    .attr("r", (d) => d.size)
    .call(initforce.drag);

  selection.append('text')
    .attr("x", (d) => d.size + 5)
    .attr("dy", ".35em")
    .text((d) => d.key);
};

var updateNode = (selection) => {
  selection.attr("transform", (d) => {
    console.log(d);
    return "translate(" + d.x + "," + d.y + ")"
  });
};

var enterLink = (selection) => {
  selection.classed('link', true)
    .attr("stroke-width", (d) => d.size);
};

var updateLink = (selection) => {
  selection.attr("x1", (d) => d.source.x)
    .attr("y1", (d) => d.source.y)
    .attr("x2", (d) => d.target.x)
    .attr("y2", (d) => d.target.y);
};

var updateGraph = (selection) => {
  selection.selectAll('.node')
    .call(updateNode);
  selection.selectAll('.link')
    .call(updateLink);
};

// *****************************************************
// ** Graph and App components
// *****************************************************

export class Graph extends React.Component {
  componentDidMount() {
    this.d3Graph = d3.select(ReactDOM.findDOMNode(this.refs.graph));
    console.log(this.d3Graph);
    force.on('tick', () => {
      // after force calculation starts, call updateGraph
      // which uses d3 to manipulate the attributes,
      // and React doesn't have to go through lifecycle on each tick
      this.d3Graph.call(updateGraph);
    });
  }

  shouldComponentUpdate(nextProps) {
    this.d3Graph = d3.select(ReactDOM.findDOMNode(this.refs.graph));

    var d3Nodes = this.d3Graph.selectAll('.node')
      .data(nextProps.nodes, (node) => node.key);
    d3Nodes.enter().append('g').call(enterNode);
    d3Nodes.exit().remove();
    d3Nodes.call(updateNode);

    var d3Links = this.d3Graph.selectAll('.link')
      .data(nextProps.links, (link) => link.key);
    d3Links.enter().insert('line', '.node').call(enterLink);
    d3Links.exit().remove();
    d3Links.call(updateLink);

    // we should actually clone the nodes and links
    // since we're not supposed to directly mutate
    // props passed in from parent, and d3's force function
    // mutates the nodes and links array directly
    // we're bypassing that here for sake of brevity in example
    force.nodes(nextProps.nodes).links(nextProps.links);
    force.start();

    return false;
  }

  render() {
    return (
      <svg width={width} height={height}>
        <g ref='graph' />
      </svg>
    );
  }
};

const PersonLink = props => (
  <li>
    <Link href="/person/[name]" as={`/person/${props.name}`}>
      <a>{props.name}</a>
    </Link>
  </li>
);

const Index = props => (
    <div>
      <ForceGraph simulationOptions={{ height: 300, width: 300 }}>
        {props.shows.map(person => (
          <ForceGraphNode node={{ id: person.id, name: person.name }} fill="red" />
        ))}
      </ForceGraph>
      <ForceGraphLink link={{ source: 'first-node', target: 'second-node' }} />
      <ul>
        <PersonLink name="Lev Parnas" />
        <PersonLink name="Donald Trump" />
        <PersonLink name="Jared Kushner" />
      </ul>
      <ul>
      {props.shows.map(person => (
        <li key={person.id}>
          <Link href="/person/[name]" as={`/person/${person.name}`}>
            <a>{person.name}</a>
          </Link>
        </li>
      ))}
    </ul>
    <Graph nodes={props.nodes} links={props.links} />
    </div>
);

Index.getInitialProps = async function() {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman');
  const data = await res.json();

  console.log(`Show data fetched. Count: ${data.length}`);

  return {
    props: [],
    shows: data.map(entry => entry.show)
  };
};

export default Index;
